<?phpnamespace app\Html;class BootsrapForm extends Form {    protected function tage($html){        return "<div class=\"form-group\">{$html}</div>";    }    public function input($name){   return $this->tage(            '<label>' .$name .'</label>    <input type="text" name="' . $name .'"value="'. $this->getValue($name)     .'"class="form-control">');        // fonction submit    }    public function submit(){        return $this->tage('<button type="submit" class="btn btn-success">Envoyer</button>');    }}